$.get("img/sprite.svg", function(data) {
    var div = document.createElement("div");
    div.hidden = !0, div.innerHTML = new XMLSerializer().serializeToString(data.documentElement), 
    document.body.insertBefore(div, document.body.childNodes[0]);
}), $.each($(".img-to-bg img"), function() {
    var cssValues = {
        background: "url(" + $(this).attr("src") + ") no-repeat center center scroll",
        "background-size": "cover"
    };
    $(this).hide(), $(this).parent().css(cssValues);
}), $(".navbar-toggle").click(function() {
    $(this).toggleClass("active"), $(".navbar-nav").slideToggle("750");
}), $(".owl-carousel").owlCarousel({
    items: 1,
    mouseDrag: !1,
    touchDrag: !1,
    pullDrag: !1,
    thumbs: !0,
    thumbImage: !0
}), $("#contact-form").validate(), $("#comment-form").validate(), $("[type=tel]").length > 0 && $("[type=tel]").mask("+(999) 999-9999", {
    placeholder: "+(___) ___-____"
}), $.each($(".image-to-bg"), function() {
    var cssValues = {
        background: "url(" + $(this).attr("src") + ") no-repeat center center scroll",
        "background-size": "cover"
    };
    $(this).hide(), $(this).parent().css(cssValues);
});

var hashTagActive = "";

$(".scroll-more").on("click touchstart", function(event) {
    if (hashTagActive != this.hash) {
        event.preventDefault();
        var dest = 0;
        dest = $(this.hash).offset().top > $(document).height() - $(window).height() ? $(document).height() - $(window).height() : $(this.hash).offset().top, 
        $("html,body").animate({
            scrollTop: dest
        }, 600, "linear"), hashTagActive = this.hash;
    }
});